--
-- Table structure for table `penalaran_matematika`
--

CREATE TABLE `penalaran_matematika` (
  `id` int(255) NOT NULL,
  `text` text NOT NULL,
  `soal` text NOT NULL,
  `pilihan_a` text NOT NULL,
  `pilihan_b` text NOT NULL,
  `pilihan_c` text NOT NULL,
  `pilihan_d` text NOT NULL,
  `pilihan_e` text NOT NULL,
  `gambar` varchar(255) NOT NULL,
  `jawaban` varchar(255) NOT NULL,
  `bobot` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `penalaran_matematika`
--

INSERT INTO `penalaran_matematika` (`id`, `text`, `soal`, `pilihan_a`, `pilihan_b`, `pilihan_c`, `pilihan_d`, `pilihan_e`, `gambar`, `jawaban`, `bobot`) VALUES
(1, '<p align=\"justify\">Permintaan jastip atau jasa titip cukup tinggi di Indonesia, salah satunya dalam hal pembelian tiket \r\nkonser. Rani, seorang penggemar K-pop, ingin membeli tiket konser untuk pertama kalinya. Karena takut gagal \r\njika membeli sendiri, Rani memutuskan menggunakan jastip. Ia membandingkan beberapa akun jastip \r\npembelian tiket untuk mengetahui kemungkinan keberhasilannya. Berikut data hasil perbandingannya.</p>\r\n<p align=\"justify\">Banyaknya transaksi yang dilakukan berarti banyaknya \r\norang-orang yang memesan tiket dengan mengisi form \r\npembelian. Sementara itu, banyaknya transaksi yang berhasil \r\nberarti banyaknya tiket yang berhasil dibeli dan diberikan \r\nkepada pelanggan yang sudah memesan.</p>', 'Dari data tersebut, urutan empat akun dengan peluang \r\nkeberhasilan tertinggi yang bisa dipertimbangkan oleh Rani \r\nadalah…', 'D, G, E, dan F\r\n', 'D, G, E, dan C\r\n', 'G, E, C dan B', 'G, E, F dan C', 'F, E, D, dan G', 'n1.jpg', 'A', 20),
(2, '<p align=\"justify\">Permintaan jastip atau jasa titip cukup tinggi di Indonesia, salah satunya dalam hal pembelian tiket \r\nkonser. Rani, seorang penggemar K-pop, ingin membeli tiket konser untuk pertama kalinya. Karena takut gagal \r\njika membeli sendiri, Rani memutuskan menggunakan jastip. Ia membandingkan beberapa akun jastip \r\npembelian tiket untuk mengetahui kemungkinan keberhasilannya. Berikut data hasil perbandingannya.</p>\r\n<p align=\"justify\">Banyaknya transaksi yang dilakukan berarti banyaknya \r\norang-orang yang memesan tiket dengan mengisi form \r\npembelian. Sementara itu, banyaknya transaksi yang berhasil \r\nberarti banyaknya tiket yang berhasil dibeli dan diberikan \r\nkepada pelanggan yang sudah memesan.</p>', 'Dari data tersebut, pernyataan yang BENAR adalah…\r\n<br>\r\n(1) Persentase keberhasilan akun A lebih besar dari akun B.\r\n<br>\r\n(2) Persentase keberhasilan akun C lebih kecil dari akun D.\r\n<br> \r\n(3) Persentase keberhasilan akun E lebih kecil dari akun F.\r\n<br> \r\n(4) Persentase keberhasilan akun E bukanlah yang paling besar.\r\n<br>\r\n(5) Persentase keberhasilan akun D adalah yang paling kecil.', '(1), (2), dan (3) saja yang benar', '(1) dan (3) saja yang benar', '(2) dan (4) saja yang benar', '(5) saja yang benar', '(1), (2), (3), (4), dan (5) yang benar', 'n1.jpg', 'C', 30),
(3, '<p align=\"justify\">Permintaan jastip atau jasa titip cukup tinggi di Indonesia, salah satunya dalam hal pembelian tiket \r\nkonser. Rani, seorang penggemar K-pop, ingin membeli tiket konser untuk pertama kalinya. Karena takut gagal \r\njika membeli sendiri, Rani memutuskan menggunakan jastip. Ia membandingkan beberapa akun jastip \r\npembelian tiket untuk mengetahui kemungkinan keberhasilannya. Berikut data hasil perbandingannya.</p>\r\n<p align=\"justify\">Banyaknya transaksi yang dilakukan berarti banyaknya \r\norang-orang yang memesan tiket dengan mengisi form \r\npembelian. Sementara itu, banyaknya transaksi yang berhasil \r\nberarti banyaknya tiket yang berhasil dibeli dan diberikan \r\nkepada pelanggan yang sudah memesan.</p>', 'Jika Rani memesan empat tiket dari akun G, peluang Rani berhasil mendapatkan tiga tiket adalah', '0, 0729', '0, 1574', '0, 2916', '0, 5852', '0, 9203', 'n1.jpg', 'C', 30),
(4, '<p align=\"justify\">Pajak Penghasilan (PPh) berdasarkan UU PPh didefinisakan sebagai pajak yang dikenakan kepada \r\norang pribadi atau badan atas penghasilan yang diperoleh dalam tahun pajak. Untuk Penghasilan Tidak Kena Pajak (PTKP), karyawan yang memiliki status belum menikah dan tidak memiliki tanggungan adalah \r\nRp54.000.000,00 per tahun. PPh termasuk ke dalam pajak progresif \r\nsehingga tarif pajak yang akan makin naik sesuai dengan naiknya dasar \r\npengenaan pajak. Diketahui perhitungan tarif PPh berdasarkan pasal 21 \r\nsesuai UU PPh didasari oleh Penghasilan Kena Pajak (PKP) dan disajikan \r\ndalam tabel berikut.</p>\r\n<p align=\"justify\">Sebagai contoh, untuk penghasilan Andi setelah dikurangi PTKP adalah \r\nRp300.000.000,00 per tahun, maka dikenakan tarif pajak sampai lapis \r\nketiga, yaitu 25%. Pada tarif lapis pertama, Rp60.000.000,00 pertama \r\ndikenakan tarif pajak 5%. Kemudian, pada tarif lapis kedua, \r\nRp190.000.000,00 berikutnya (dari 60 juta sampai 250 juta) dikenakan tarif pajak 15%. Selanjutnya, pada tarif \r\nlapis terakhir, sisa Rp50.000.000 dikenakan tarif pajak 25%. </p>', 'Diketahui Egi adalah seorang karyawan tetap PT X dengan status belum menikah dan tidak memiliki \r\ntanggungan dengan penghasilan netto sebesar Rp16.000.000,00 per bulan. Tarif pajak tahunan yang \r\nharus dibayarkan Egi adalah…', 'Rp6.900.000,00', 'Rp9.600.000,00', 'Rp11.700.000,00', 'Rp14.700.000,00', 'Rp20.700.000,00', 'n4.jpg', 'D', 40),
(5, '<p align=\"justify\">Pajak Penghasilan (PPh) berdasarkan UU PPh didefinisakan sebagai pajak yang dikenakan kepada \r\norang pribadi atau badan atas penghasilan yang diperoleh dalam tahun pajak. Untuk Penghasilan Tidak Kena Pajak (PTKP), karyawan yang memiliki status belum menikah dan tidak memiliki tanggungan adalah \r\nRp54.000.000,00 per tahun. PPh termasuk ke dalam pajak progresif \r\nsehingga tarif pajak yang akan makin naik sesuai dengan naiknya dasar \r\npengenaan pajak. Diketahui perhitungan tarif PPh berdasarkan pasal 21 \r\nsesuai UU PPh didasari oleh Penghasilan Kena Pajak (PKP) dan disajikan \r\ndalam tabel berikut.</p>\r\n<p align=\"justify\">Sebagai contoh, untuk penghasilan Andi setelah dikurangi PTKP adalah \r\nRp300.000.000,00 per tahun, maka dikenakan tarif pajak sampai lapis \r\nketiga, yaitu 25%. Pada tarif lapis pertama, Rp60.000.000,00 pertama \r\ndikenakan tarif pajak 5%. Kemudian, pada tarif lapis kedua, \r\nRp190.000.000,00 berikutnya (dari 60 juta sampai 250 juta) dikenakan tarif pajak 15%. Selanjutnya, pada tarif \r\nlapis terakhir, sisa Rp50.000.000 dikenakan tarif pajak 25%. </p>', 'John adalah seorang guru dengan status belum menikah dan tidak memiliki tanggungan dengan \r\npenghasilan netto sebesar Rp4.000.000,00 per bulan. Tarif pajak tahunan yang harus dibayar oleh \r\nJohn adalah…', 'Rp0,00', 'Rp200.000,00', 'Rp300.000,00', 'Rp2.400.000,00', 'Rp2.700.000,00', 'n4.jpg', 'A', 40),
(6, '<p align=\"justify\">Pajak Penghasilan (PPh) berdasarkan UU PPh didefinisakan sebagai pajak yang dikenakan kepada \r\norang pribadi atau badan atas penghasilan yang diperoleh dalam tahun pajak. Untuk Penghasilan Tidak Kena Pajak (PTKP), karyawan yang memiliki status belum menikah dan tidak memiliki tanggungan adalah \r\nRp54.000.000,00 per tahun. PPh termasuk ke dalam pajak progresif \r\nsehingga tarif pajak yang akan makin naik sesuai dengan naiknya dasar \r\npengenaan pajak. Diketahui perhitungan tarif PPh berdasarkan pasal 21 \r\nsesuai UU PPh didasari oleh Penghasilan Kena Pajak (PKP) dan disajikan \r\ndalam tabel berikut.</p>\r\n<p align=\"justify\">Sebagai contoh, untuk penghasilan Andi setelah dikurangi PTKP adalah \r\nRp300.000.000,00 per tahun, maka dikenakan tarif pajak sampai lapis \r\nketiga, yaitu 25%. Pada tarif lapis pertama, Rp60.000.000,00 pertama \r\ndikenakan tarif pajak 5%. Kemudian, pada tarif lapis kedua, \r\nRp190.000.000,00 berikutnya (dari 60 juta sampai 250 juta) dikenakan tarif pajak 15%. Selanjutnya, pada tarif \r\nlapis terakhir, sisa Rp50.000.000 dikenakan tarif pajak 25%. </p>', 'Anis adalah seorang karyawan tetap PT y dengan status belum menikah dan tidak memiliki tanggungan \r\ndengan tarif pajak tahunan yang harus dibayarkan Anis adalah Rp4.800.000,00. Penghasilan netto Anis \r\nper bulan di PT Y adalah…', 'Rp11.500.000,00', 'Rp11.250.000,00', 'Rp11.000.000,00', 'Rp10.750.000,00', 'Rp10.500.000,00', 'n4.jpg', 'E', 50),
(7, '<p align=\"justify\">Pajak Penghasilan (PPh) berdasarkan UU PPh didefinisakan sebagai pajak yang dikenakan kepada \r\norang pribadi atau badan atas penghasilan yang diperoleh dalam tahun pajak. Untuk Penghasilan Tidak Kena Pajak (PTKP), karyawan yang memiliki status belum menikah dan tidak memiliki tanggungan adalah \r\nRp54.000.000,00 per tahun. PPh termasuk ke dalam pajak progresif \r\nsehingga tarif pajak yang akan makin naik sesuai dengan naiknya dasar \r\npengenaan pajak. Diketahui perhitungan tarif PPh berdasarkan pasal 21 \r\nsesuai UU PPh didasari oleh Penghasilan Kena Pajak (PKP) dan disajikan \r\ndalam tabel berikut.</p>\r\n<p align=\"justify\">Sebagai contoh, untuk penghasilan Andi setelah dikurangi PTKP adalah \r\nRp300.000.000,00 per tahun, maka dikenakan tarif pajak sampai lapis \r\nketiga, yaitu 25%. Pada tarif lapis pertama, Rp60.000.000,00 pertama \r\ndikenakan tarif pajak 5%. Kemudian, pada tarif lapis kedua, \r\nRp190.000.000,00 berikutnya (dari 60 juta sampai 250 juta) dikenakan tarif pajak 15%. Selanjutnya, pada tarif \r\nlapis terakhir, sisa Rp50.000.000 dikenakan tarif pajak 25%. </p>', 'Nisa adalah seorang karyawan tetap di suatu perusahaan dengan status belum menikah dan tidak \r\nmemiliki tanggungan dengan penghasilan netto sebesar Rp42.500.000,00 per bulan. Persentase tarif \r\nPPh progresif yang dikenakan kepada Nisa adalah sampai…', '5%', '15%', '25%', '30%', '35%', 'n4.jpg', 'C', 50),
(8, '<img src=\"https://latex.codecogs.com/svg.image?_{Z}^{A}\\textrm{Y}\" border=\"0\"/><br>\r\n<p align=\"justify\">Y: lambang atom<br>\r\nA: nomor massa<br>\r\nZ: nomor atom atau jumlah proton<br>\r\nIsoton adalah atom-atom yang memiliki jumlah neutron sama banyak. Jumlah neutron dapat dihitung dengan \r\nmenggunakan persamaan berikut.\r\nNomor massa = jumlah proton + jumlah neutron</p>', 'Isoton dari atom <img src=\"https://latex.codecogs.com/svg.image?_{28}^{58}\\textrm{Ni}\" border=\"0\"/> adalah…', '<img src=\"https://latex.codecogs.com/svg.image?_{14}^{29}\\textrm{Si}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?_{18}^{40}\\textrm{Ar}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?_{25}^{55}\\textrm{Mn}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?_{26}^{58}\\textrm{Fe}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?_{28}^{60}\\textrm{Ni}\" border=\"0\"/>', '', 'C', 50),
(9, '<img src=\"https://latex.codecogs.com/svg.image?_{Z}^{A}\\textrm{Y}\" border=\"0\"/><br>\r\n<p align=\"justify\">Y: lambang atom<br>\r\nA: nomor massa<br>\r\nZ: nomor atom atau jumlah proton<br>\r\nIsoton adalah atom-atom yang memiliki jumlah neutron sama banyak. Jumlah neutron dapat dihitung dengan \r\nmenggunakan persamaan berikut.\r\nNomor massa = jumlah proton + jumlah neutron</p>', 'Diketahui merupakan <img src=\"https://latex.codecogs.com/svg.image?_{11}^{23}\\textrm{Na}\" border=\"0\"/> isoton dari atom <img src=\"https://latex.codecogs.com/svg.image?_{20+p}^{20-p}\\textrm{X}\" border=\"0\"/>. Tentukan nilai kebenaran dari pernyataan-pernyataan berikut!<br>\r\n1. Nilai dari p adalah 6. <br>\r\n2. X bukanlah isoton dari <img src=\"https://latex.codecogs.com/svg.image?_{18}^{40}\\textrm{Ar}\" border=\"0\"/> <br>\r\n3. Jumlah neutron dari atom X adalah 12.<br>\r\n4. Nomor massa dari atom X kurang dari 20.', '1.Salah 2.Benar 3.Benar 4.Salah', '1.Benar 2.Benar 3.Benar 4.Salah', '1.Salah 2.Benar 3.Benar 4.Benar', '1.Benar 2.Benar 3.Benar 4.Benar', '1.Benar 2.Salah 3.Salah 4.Benar', '', 'A', 50),
(10, '<img src=\"https://latex.codecogs.com/svg.image?_{Z}^{A}\\textrm{Y}\" border=\"0\"/><br>\r\n<p align=\"justify\">Y: lambang atom<br>\r\nA: nomor massa<br>\r\nZ: nomor atom atau jumlah proton<br>\r\nIsoton adalah atom-atom yang memiliki jumlah neutron sama banyak. Jumlah neutron dapat dihitung dengan \r\nmenggunakan persamaan berikut.\r\nNomor massa = jumlah proton + jumlah neutron</p>\r\n<p align=\"justify\">Diketahui pula bahwa ion adalah atom yang memiliki muatan. Besar muatan umumnya dapat \r\nditemukan dari notasi yang ditulis pada bagian kanan atas ion. Sebagai contoh, besar muatan dari ion \r\n𝑆<sup>2−</sup>, 𝐶𝑙<sup>−</sup>, 𝑁𝑎<sup>+</sup>,dan 𝐶𝑎<sup>2+</sup> secara berurutan adalah -2, -1, +1, dan +2.\r\nJumlah elektron dari ion dapat dihitung dengan persamaan berikut.\r\nJumlah elektron = jumlah proton – muatan</p>', 'Jumlah proton, elektron, dan neutron dari ion <img src=\"https://latex.codecogs.com/svg.image?_{19}^{39}\\textrm{K}\" border=\"0\"/><sup>+</sup>secara berurutan adalah…', '19, 20, 18', '20, 19. 18', '18, 19, 39', '19, 39, 18', '19, 18, 20', '', 'E', 50),
(11, '<p align=\"justify\">Tujuh buah silinder dengan jari-jari 7 cm disusun dan dibungkus sehingga membentuk \r\ntampak atas seperti gambar berikut.</p>', 'Jika volume total silinder sebesar 10.780 𝑐𝑚<sup>3</sup>\r\n. Volume antarruang yang terbentuk sebesar…\r\n<b>Catatan: gunakan 𝜋 = </b><img src=\"https://latex.codecogs.com/svg.image?\\frac{22}{7}\" border=\"0\"/>', '(2.940√3 − 3.360) 𝑐𝑚<sup>3</sup>', '2.520 𝑐𝑚<sup>3</sup>\r\n', '600√3 𝑐𝑚<sup>3</sup>\r\n', '(294√3 − 336) 𝑐𝑚<sup>3</sup>', '(294√3 − 3.360) 𝑐𝑚<sup>3</sup>', 'n11.jpg', 'A', 60),
(12, '<p align=\"justify\">Tujuh buah silinder dengan jari-jari 7 cm disusun dan dibungkus sehingga membentuk \r\ntampak atas seperti gambar berikut.</p>', 'Jika ketujuh silinder tersebut dililit beberapa lapis oleh tali yang panjangnya 768 cm, banyak lilitan \r\nyang dibuat adalah… lapis.\r\n<b>Catatan: gunakan 𝜋 = </b><img src=\"https://latex.codecogs.com/svg.image?\\frac{22}{7}\" border=\"0\"/>', '2', '3', '4', '6', '9', 'n11.jpg', 'D', 60),
(13, '<p align=\"justify\">Tujuh buah silinder dengan jari-jari 7 cm disusun dan dibungkus sehingga membentuk \r\ntampak atas seperti gambar berikut.</p>', 'Jika luas selimut bangun ruang yang dimaksud sebesar 704 𝑐𝑚<sup>2</sup>\r\n, tinggi masing-masing tabung adalah \r\nsebesar … cm.\r\n\r\n<b>Catatan: gunakan 𝜋 = </b><img src=\"https://latex.codecogs.com/svg.image?\\frac{22}{7}\" border=\"0\"/>', '2', '3', '4', '6', '9', 'n11.jpg', 'E', 60),
(14, '', '<b>Perhatikan teks berikut!</b><p align=\"justify\">\r\nTenaga kerja adalah penduduk berusia 15 – 64 tahun atau penduduk usia produktif. Tenaga \r\nkerja dibagi menjadi angkatan kerja dan bukan angkatan kerja. Adapun angkatan kerja terdiri atas \r\norang yang bekerja dan orang yang sedang mencari pekerjaan (pada saat ini tidak bekerja). Adapun \r\nbukan angkatan kerja adalah orang yang berada di usia kerja, tetapi memilih tidak bekerja. Orang yang \r\nbekerja dibedakan menjadi orang yang bekerja penuh dan setengah menganggur.</p>\r\n<p align=\"justify\">Di suatu negara diketahui jumlah orang yang sedang mencari pekerjaan adalah 20% dari \r\nangkatan kerjanya. Adapun orang yang bekerja sebanyak 6,4 juta jiwa dan jumlah angkatan kerjanya \r\n80% dari total tenaga kerja di negara tersebut. </p>\r\nBerdasarkan teks tersebut, banyaknya penduduk yang dikategorikan sebagai bukan angkatan kerja \r\nadalah…', '1,6 juta jiwa', '2 juta jiwa', '4,8 juta jiwa', '6,4 juta jiwa', '8 juta jiwa', '', 'B', 60),
(15, '', '<b>Perhatikan teks berikut!</b>\r\n<p align=\"justify\">\r\nTenaga kerja adalah penduduk berusia 15 – 64 tahun atau penduduk usia produktif. Tenaga \r\nkerja dibagi menjadi angkatan kerja dan bukan angkatan kerja. Adapun angkatan kerja terdiri atas \r\norang yang bekerja dan orang yang sedang mencari pekerjaan (pada saat ini tidak bekerja). Adapun \r\nbukan angkatan kerja adalah orang yang berada di usia kerja, tetapi memilih tidak bekerja. Orang yang \r\nbekerja dibedakan menjadi orang yang bekerja penuh dan setengah menganggur.</p>\r\n<p align=\"justify\">\r\nDi suatu negara diketahui jumlah orang yang sedang mencari pekerjaan adalah 20% dari \r\nangkatan kerjanya. Adapun jumlah angkatan kerjanya 80% dari total tenaga kerja di negara tersebut. </p>\r\nDari penduduk di negara tersebut yang masuk kategori tenaga kerja, dipilih satu orang secara acak. \r\nJika diketahui bahwa saat ini orang tersebut tidak bekerja, peluang orang tersebut sedang mencari \r\npekerjaan adalah…', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{4}{9}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{2}{5}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{5}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{8}{9}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{3}\" border=\"0\"/>', '', 'A', 60),
(16, '', '<b>Perhatikan teks berikut!</b>\r\n<p align=\"justify\">\r\nTenaga kerja adalah penduduk berusia 15 – 64 tahun atau penduduk usia produktif. Tenaga \r\nkerja dibagi menjadi angkatan kerja dan bukan angkatan kerja. Adapun angkatan kerja terdiri atas \r\norang yang bekerja dan orang yang sedang mencari pekerjaan (pada saat ini tidak bekerja). Adapun \r\nbukan angkatan kerja adalah orang yang berada di usia kerja, tetapi memilih tidak bekerja. Orang yang \r\nbekerja dibedakan menjadi orang yang bekerja penuh dan setengah menganggur.</p>\r\n<p align=\"justify\">\r\nMenurut Data Statistika pada tahun 2022, perkiraan jumlah angkatan kerja di seluruh dunia \r\nsekitar 3,32 miliar jiwa dan hanya 1,3 miliar di antaranya adalah pekerja perempuan. Hal yang sama \r\njuga terjadi di Indonesia. Menurut Badan Pusat Satitstik (BPS), dari total 144 juta angkatan kerja di \r\ntahun ini, 60% di antaranya masih didominasi oleh laki-laki.</p>\r\nBerdasarkan teks tersebut, persentase angka pekerja wanita dari Indonesia tahun 2022 dibandingkan \r\nangka pekerja wanita di dunia adalah…\r\n', '1,39%', '2,46%', '4,35', '5,78', '4,43', '', 'E', 60),
(17, '', '<b>Perhatikan teks berikut!</b><p align=\"justify\">\r\nTenaga kerja adalah penduduk berusia 15 – 64 tahun atau penduduk usia produktif. Tenaga kerja \r\ndibagi menjadi angkatan kerja dan bukan angkatan kerja. Adapun angkatan kerja terdiri atas orang \r\nyang bekerja dan orang yang sedang mencari pekerjaan (pada saat ini tidak bekerja). Adapun bukan \r\nangkatan kerja adalah orang yang berada di usia kerja, tetapi memilih tidak bekerja. Orang yang \r\nbekerja dibedakan menjadi orang yang bekerja penuh dan setengah menganggur.\r\nMenurut Data Badan Pusat Statistik (BPS), jumlah angkatan kerja wanita di Indonesia saat ini masih \r\ntertinggal dari angkatan kerja pria. Tahun ini tercatat sekitar 144 juta angkatan kerja di Indonesia dan \r\n40% di antaranya adalah pekerja wanita.\r\nSuatu perusahaan retail di Belanda berencana memperluar pasarnya di Indonesia. Perusahaan \r\nmembutuhkan bantuan 4 pekerja dari negara tersebut dan 2 di antaranya adalah pekerja wanita. Dari \r\ndata lamaran yang diterima, 5 × 10<sup>−5</sup>% dari total angkatan kerja di Indonesia lolos seleksi dan 42 di \r\nantaranya adalah pekerja wanita. </p>\r\nBerdasarkan teks tersebut, banyaknya susunan pekerja yang mungkin diterima adalah… pekerja\r\n', '563.726', '374.535', '273.547', '189.335', '89.349', '', 'B', 70),
(18, '<p align=\"justify\">Sebagian kalangan ilmuwan tertarik untuk meneliti lebih lanjut tentang barisan Fibonacci. Barisan ini dikatakan \r\nbanyak memiliki keunikan, seperti disebut sebagai golden ratio, memiliki kesesuaian dengan komposisi tubuh \r\nmanusia, memiliki proporsi yang pas dengan alam, bahkan dapat merumuskan di mana pusat dunia. Barisan \r\nFibonacci sendiri adalah barisan bilangan yang diperoleh dengan menjumlahkan dua bilangan sebelumnya \r\ndengan bilangan pertama dan kedua berturut-turut adalah 1 dan 1. ', 'Berdasarkan informasi tersebut, bilangan ke-14 pada barisan Fibonacci adalah..\r\n', '244', '277', '367', '377', '610', '', 'D', 70),
(19, '<p align=\"justify\">Sebagian kalangan ilmuwan tertarik untuk meneliti lebih lanjut tentang barisan Fibonacci. Barisan ini dikatakan \r\nbanyak memiliki keunikan, seperti disebut sebagai golden ratio, memiliki kesesuaian dengan komposisi tubuh \r\nmanusia, memiliki proporsi yang pas dengan alam, bahkan dapat merumuskan di mana pusat dunia. Barisan \r\nFibonacci sendiri adalah barisan bilangan yang diperoleh dengan menjumlahkan dua bilangan sebelumnya \r\ndengan bilangan pertama dan kedua berturut-turut adalah 1 dan 1. ', 'Berdasarkan informasi tersebut, jumlah bilangan ke-9 dan ke-15 pada barisan Fibonacci adalah..', '623', '631', '644', '665', '699', '', 'C', 80),
(20, '<p align=\"justify\">Sebagian kalangan ilmuwan tertarik untuk meneliti lebih lanjut tentang barisan Fibonacci. Barisan ini dikatakan \r\nbanyak memiliki keunikan, seperti disebut sebagai golden ratio, memiliki kesesuaian dengan komposisi tubuh \r\nmanusia, memiliki proporsi yang pas dengan alam, bahkan dapat merumuskan di mana pusat dunia. Barisan \r\nFibonacci sendiri adalah barisan bilangan yang diperoleh dengan menjumlahkan dua bilangan sebelumnya \r\ndengan bilangan pertama dan kedua berturut-turut adalah 1 dan 1. ', 'Berdasarkan informasi tersebut, selisih bilangan ke-98 dan ke-100 dari barisan Fibonacci merupakan \r\npenjumlahan… dari barisan Fibonacci', 'Bilangan ke-97 dan bilangan ke-98', 'Bilangan ke-98 dan bilangan ke-99', 'Bilangan ke-97 dan bilangan ke-99', 'Bilangan ke-97, bilangan ke-98 dan bilangan ke-99', 'Bilangan ke-98, bilangan ke-99 dan bilangan ke-100', '', 'A', 80);

-- --------------------------------------------------------

--
-- Table structure for table `penalaran_umum`
--

CREATE TABLE `penalaran_umum` (
  `id` int(255) NOT NULL,
  `soal` text NOT NULL,
  `pilihan_a` text NOT NULL,
  `pilihan_b` text NOT NULL,
  `pilihan_c` text NOT NULL,
  `pilihan_d` text NOT NULL,
  `pilihan_e` text NOT NULL,
  `gambar` varchar(255) NOT NULL,
  `jawaban` varchar(255) NOT NULL,
  `bobot` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Dumping data for table `penalaran_umum`
--

INSERT INTO `penalaran_umum` (`id`, `soal`, `pilihan_a`, `pilihan_b`, `pilihan_c`, `pilihan_d`, `pilihan_e`, `gambar`, `jawaban`, `bobot`) VALUES
(1, 'Perhatikan teks berikut!<br>\r\nStudi yang dilakukan oleh para peneliti di University of Toronto itu menemukan bahwa konsumsi \r\nmadu mentah memiliki efek paling positif pada tubuh. Dampak positif konsumsi madu mentah itu \r\nadalah penurunan glukosa darah dan jumlah lipoprotein densitas rendah (kolesterol jahat) dalam \r\ndarah. Dilansir dari Medical News Today, madu kemasan pabrik biasanya sudah melalui proses \r\npasteurisasi, sedangkan madu mentah yang langsung diambil dari peternakan tidak mengalami \r\nproses tersebut.<br>\r\nBerdasarkan teks tersebut, pernyataan yang PASTI SALAH adalah …<br>\r\n(1) Mengonsumsi madu mentah dapat menaikkan kadar glukosa darah dalam tubuh.<br>\r\n(2) Mengonsumsi madu mentah memiliki efek negatif untuk kesehatan tubuh.<br>\r\n(3) Mengonsumsi madu mentah dapat meningkatkan risiko diabetes melitus.\r\n<br> (4) Mengonsumsi madu mentah dapat menurunkan jumlah kolesterol jahat.<br>\r\n(5) Mengonsumsi madu pasteurisasi dapat meningkatkan kesehatan tubuh.', 'Pernyataan (2) dan Pernyataan (3)', 'Hanya pernyataan (1)', 'Pernyataan (2) dan Pernyataan (4)', 'Pernyataan (1) dan Pernyataan (2)', 'Pernyataan (1), Pernyataan (2), dan Pernyataan (5)', '', 'D', 30),
(2, 'Perhatikan teks berikut!<br>\r\nPT Pertamina kembali menyesuaikan harga bahan bakar minyak (BBM) nonsubsidi per Kamis, 1 \r\nDesember 2022. BBM nonsubsidi yang mengalami penyesuaian harga adalah jenis Pertamax \r\nTurbo (RON 98), Dexlite (CN 51), dan Pertamina Dex (CN 53). Dikutip dari laman MyPertamina,\r\nharga Pertamax Turbo naik dari Rp 14.300-Rp 14.900 per liter menjadi Rp 15.200-Rp 15.800 per \r\nliter. Harga Dexlite dari semula Rp 18.000-Rp 18.700 per liter naik menjadi Rp 18.300-Rp 19.000 \r\nper liter. Sementara itu, harga Pertamina Dex yang semula Rp 18.550-Rp 19.350 per liter naik \r\nmenjadi Rp 18.800-Rp 19.600 per liter.\r\nHarga BBM dinyatakan dalam rentang tertentu karena harga di sejumlah pom bensin berbeda-beda. Diasumsikan kenaikan harga bernilai sama di semua pom bensin.<br> Berdasarkan teks tersebut, \r\npernyataan yang PASTI BENAR adalah … ', 'PT Pertamina kembali menyesuaikan harga empat jenis BBM nonsubsidi.', 'BBM nonsubsidi jenis dexlite mengalami kenaikan harga sebesar Rp900,00.\r\n', 'BBM nonsubsidi jenis pertamina dex mengalami kenaikan harga sebesar Rp300,00.\r\n', 'BBM nonsubsidi jenis pertamax turbo mengalami kenaikan harga paling besar.\r\n', 'BBM nonsubsidi pertama kalinya mengalami penyesuaian harga di bulan Desember 2022', '', 'D', 30),
(3, 'Perhatikan teks berikut!<br>\r\nCEO Tesla Elon Musk akan melakukan uji coba cip otak nirkabel Neuralink pada manusia dalam \r\nenam bulan ke depan. Menurut Musk, cip tersebut memungkinan pasien yang cacat untuk \r\nbergerak dan berkomunikasi lagi, dikutip dari Reuters. Berbasis di San Francisco Bay Area dan \r\nAustin, Texas, Neuralink dalam beberapa tahun terakhir telah melakukan tes pada hewan guna \r\nmendapatkan persetujuan dari Food and Drug Administration (FDA) AS untuk memulai uji klinis \r\npada manusia.<br>\r\nPernyataan yang PALING MUNGKIN BENAR berdasarkan teks tersebut adalah …', 'CEO Tesla Elon Musk akan melakukan uji coba cip otak nirkabel Neuralink pada manusia dalam \r\nenam bulan ke depan.', 'Neuralink melakukan uji klinis terhadap tumbuhan untuk mendapatkan persetujuan dari food and drug administration. ', 'Menurut Musk, cip otak memungkinkan orang yang mengalami kelumpuhan dapat berjalan \r\nnormal kembali.', 'Organisasi Neuralink yang memproduksi cip otak berbasis di San Francisco Bay Areas dan Austin, Meksiko.', 'CEO Tesla Elon Musk mengatakan, cip otak yang diproduksi oleh Neuralink akan langsung diuji cobakan kepada hewan. ', '', 'C', 30),
(4, 'Perhatikan teks berikut!<br>\r\nGuru Besar Fakultas Ekologi Manusia Institut Pertanian Bogor (IPB), Prof. ali Khomsan, \r\nmengatakan bahwa konsumsi sayur dan buah masyarakat Indonesia mencapai 100 gram pre \r\nkapita per hari, sedangkan anjuran WHO untuk konsumsi buah dan sayur sebanyak 400 gram per \r\nkapita per hari. \"Rata-rara kita baru memenuhi kurang lebih 25 persen dari anjuran WHO,\" kata \r\nProf. Ali seperti dikutip dari kanal YouTube IPB TV, Rabu (29/12/2021). Prof. Ali menekankan, saat \r\ndihitung menurut segmen umur, konsumsi sayur dan buah sebesar 100 gram per kapita per hari \r\nitu merata.<br>\r\nKesimpulan yang paling tepat dari teks tersebut adalah …', 'WHO menganjurkan untuk mengonsumsi buah sebanyak 400 gram per kapita per hari.', 'Masyarakat Indonesia memiliki kebiasaan memakan sayur dan buah yang baik.', 'WHO menilai bahwa konsumsi buah sebesar 100 gram per kapita per hari cukup.\r\n', 'Masyarakat Indonesia perlu mengimpor sayur dan buah agar nutrisinya terpenuhi.', 'Kebiasaan mengonsumsi sayur dan buah masyarakat Indonesia masih rendah.\r\n', '', 'E', 30),
(5, 'Perhatikan teks berikut!<br>\r\nPT X adalah perusahaan perdagangan berbasis daring atau biasa disebut sebagai e-commerce. \r\nPada tahun ini, PT X mengalami penurunan jumlah pelanggan. Menurut jajaran direksi, hal ini \r\ndiakibatkan banyak pelanggan yang beralih ke e-commerce kompetitor yang memiliki fitur belanja \r\nsaat menonton siaran langsung. Jajaran direksi meyakini bahwa banyak orang merasa lebih aman \r\ndan nyaman ketika berbelanja sembari menonton siaran langsung.<br> \r\nPernyataan yang MEMPERKUAT argumen jajaran direksi PT X adalah …', 'Pihak kepolisian memberikan data bahwa terjadi peningkatan angka penipuan saat belanja \r\ndaring.', 'Jumlah pelanggan PT X lebih rendah dibandingkan jumlah pelanggan perusahaan kompetitor.\r\n', ' Perusahaan kompetitor yang memiliki fitur live chat mengalami peningkatan jumlah \r\npelanggan.', 'Belum ada e-commerce yang mempunyai fitur siaran langsung tahun lalu.\r\n', 'PT X mengembangkan fitur belanja saat menonton siaran langsung mulai tahun depan.', '', 'D', 30),
(6, 'Perhatikan teks berikut!<br>\r\nMenteri Koordinator Bidang Kemaritiman dan Investasi (Menko Marves), Luhut Binsar Pandjaitan, \r\nmenyebut subsidi pembelian kendaraan listrik baik untuk mobil ataupun motor yang akan mulai \r\nditerapkan 2023 mendatang. Pemerintah memberikan subsidi sebagai upaya meransang daya beli \r\nmasyarakat Indonesia ke kendaraan elektrifikasi yang ramah lingkungan sembari mencapai target \r\nRI menuju Zero Emission pada 2060 mendatang. Luhut juga menyampaikan saat ini pemerintah \r\ntengah berupaya menyelesaikan skema pemberian subsidi sekitar Rp6,5 juta. <br>\r\nPernyataan yang MEMPERLEMAH argumen Luhut Binsar Pandjaitan adalah…', 'Pemberian subsidi pembelian kendaraan listrik akan dimulai pada akhir tahun 2023.', 'Pembangunan stasiun pengisian kendaraan listrik umum belum merata di Indonesia.', 'Pemerintah menargetkan Indonesia akan bebas emisi di tahun 2060 mendatang.', 'Subsidi yang diberikan pemerintah dapat merangsang daya beli masyarakat. \r\n', 'Penggunaan kendaraan listrik akan memiliki dampak positif dan negatif.', '', 'B', 30),
(7, 'Perhatikan beberapa pernyataan berikut ini!<br>\r\nPeneliti A menyatakan, daun tanaman X mengalami evolusi karena lingkungannya yang bersifat \r\nekstrem.\r\nPeneliti B menyatakan, tanaman X tidak memiliki zar hijau daun sehingga mengalami evolusi.\r\nHasil penelitian terbaru menyatakan bahwa tanaman X mengalami evolusi karena beradaptasi \r\ndengan habitatnya.<br>\r\nTentukan apakah pernyataan berikut bernilai BENAR atau SALAH!<br>\r\n1. Hasil penelitian terbaru memperkuat \r\npernyataan peneliti A.<br>\r\n2. hasil penelitian terbaru memperkuat \r\npernyataan peneliti B.<br>\r\n3. hasil penelitian terbaru memperlemah \r\npernyataan peneliti A<br>\r\n4. hasil penelitian terbaru memperlemah \r\npernyataan peneliti B.', '1.salah 2.benar 3.salah 4.benar', '1.benar 2.salah 3.benar 4.salah', '1.benar 2.benar 3.benar 4.salah', '1.salah 2.salah 3.salah 4.salah', '1.benar 2.salah 3.salah 4.salah', '', 'E', 30),
(8, 'Perhatikan teks berikut!<br>\r\nbanyak faktor yang memengaruhi keadaan suhu suatu tempat. Salah satunya adalah ketinggian. \r\nMakin tinggi suatu tempat, makin dingin pula biasanyanya tempat tersebut.<br>\r\nPernyataan berikut yang paling menjelaskan akibat dari tingginya suatu tempat adalah …', 'kebanyakan orang di pegunungan tidak membutuhkan jaket.', 'Angin bertiup dengan sangat kencang di wilayah-wilayah dataran tinggi.', 'Ada daerah yang diselimuti hawa dingin walaupun jauh dari pegunungan.', 'Banyak pendaki gunung yang menggunakan jaket karena suhu yang tinggi di wilayah gunung. ', 'Banyak warung di daerah pegunungan yang menjual aneka makanan dan minuman hangat.\r\n', '', 'E', 30),
(9, 'Perhatikan teks berikut!<br> Dilansir dari NPR, virus corona SARS-CoV-2 varian Omicron dan Delta sama-sama penyebab Covid19. Penelitian menunjukkan, Covid-19 varian Delta dua kali lebih menular apabila dibandingkan \r\nvarian sebelumnya. Selain itu, Covid-19 varian delta terbukti menyebabkan infeksi yang parah \r\ndibandingkan varian sebelumnya apabila menyerang penderita yang belum divaksinasi. Menurut \r\nWHO, Covid-19 varian omicron lebih menular dibandingkan varian lain, termasuk delta. Di \r\nbeberapa kasus, Covid-19 varian omicron menyebabkan gejala yang tidak terlalu parah. Terutama \r\npada penderita yang sudah divaksinasi, usia muda, dan tanpa komorbid atau penyakit penyerta.<br>\r\nPernyataan berikut yang paling menjelaskan perbedaan yang disebutkan pada teks adalah…', 'Covid-19 varian Omicron menyebabkan gejala yang tidak terlalu parah.', 'Covid-19 varian Omicron lebih menular dibandingkan varian Delta.', 'Virus corona SARS-CoV-2 varian Omicron dan Delta sama-sama penyebab Covid-19.', 'Penderita Covid-19 yang sudah divaksin tidak menunjukkan gejala yang parah.', 'Penderita Covid-19 yang memiliki penyakit penyerta menunjukkan gejala yang ringan.', '', 'B', 30),
(10, 'Perhatikan teks berikut!<br> Manfaat tumbuhan bagi makhluk hidup adalah menyerap karbon dioksida serta melepaskan \r\noksigen. Hewan juga membutuhkan tumbuhan sebagai sumber makanannya. Herbivor, seperti \r\nsapi, kuda, gajah, rusa, jerapah, dan panda menggantungkan hidupnya pada tumbuhan. Manusia \r\njuga memerlukan sayuran dan buah-buahan yang dihasilkan dari beberapa jenis tumbuhan, agar \r\nkebutuhan nutrisinya tercukupi. Dalam situs Byjus, dituliskan bahwa tumbuhan merupakan \r\nprodusen utama, dan seluruh makhluk hidup di Bumi menggantungkan hidupnya pada tumbuhan. \r\nSebagai contoh, tumbuhan bisa diolah menjadi obat-obatan, diambil kayunya, serta dimanfaatkan \r\nuntuk membuat pakaian. Tak hanya itu, tumbuhan yang diolah menjadi kompos bisa \r\nmenyuburkan tanah. Sementara sisa tumbuhan yang telah lapuk di atas tanah dapat dimanfaatkan \r\ncacing sebagai sumber makanan.<br>\r\nPernyataan berikut yang didukung oleh teks di atas adalah…', 'Makhluk hidup tidak akan bisa hidup di Bumi apabila tidak ada tumbuhan.', 'Tumbuhan menghasilkan karbon dioksida yang penting untuk makhluk hidup.', ' Tumbuhan selalu menggantungkan hidupnya pada manusia dan juga hewan.', 'Manusia dan hewan merupakan penyumbang karbon dioksida terbesar di Bumi.', 'Tumbuhan membutuhkan nutrisi yang cukup agar bisa tumbuh dan berkembang dengan baik.', '', 'A', 30),
(11, 'Perhatikan dua buah pernyataan berikut ini!<br>\r\n- Tidak seorangpun peserta UTBK diizinkan memotret soal-soal ujian.<br> - Sebagian siswa SMA Kasih Ibu menjadi peserta UTBK.<br>\r\nSimpulan dari kedua pernyataan di atas adalah…', 'Semua siswa yang tidak diizinkan memotret soal-soal ujian bukan siswa SMA Kasih Ibu.', 'Semua siswa SMA Kasih Ibu yang menjadi peserta UTBK tidak diizinkan memotret soal-soal ujian.', 'Sebagian siswa SMA Kasih Ibu yang menjadi peserta UTBK diizinkan memotret soal-soal ujian.', 'Sebagian siswa yang diizinkan memotret soal-soal ujian adalah siswa SMA Kasih Ibu.', 'Sebagian siswa yang bukan SMA Kasih Ibu tidak diizinkan memotret soal-soal ujian.', '', 'B', 35),
(12, ' Perhatikan beberapa pernyataan berikut!<br>\r\nJika sebuah komputer tidak layak digunakan untuk bekerja, maka proses booting-nya lama, sering \r\nmengalami not responding, dan muncul blue screen secara tiba-tiba. hal-hal tersebut membuat \r\nkomputer tidak dapat dijual kembali.<br>\r\nJika sebuah komputer Hanya sesekali mengalami not responding, proses booting sebentar, dan \r\ntidak pernah muncul blue screen, maka simpulan yang PALING TEPAT terkait komputer tersebut \r\nadalah…', 'Tidak layak digunakan untuk bekerja', 'Layak digunakan untuk bekerja', 'Layak digunakan untuk bermain', 'Tidak dapat dijual kembali', 'Dapat dijual kembali', '', 'B', 35),
(13, ' Perhatikan beberapa pernyataan berikut!<br>\r\n- Jika hujan deras mengguyur, maka jalan protokol banjir.<br>\r\n- Jika jalan protokol bajir, maka terjadi kemacetan di semua daerah.<br>\r\nSimpulan yang tepat adalah…', 'Jika hujan deras tidak mengguyur, maka terjadi kemacetan di sebagian daerah.', 'Jika hujan deras tidak mengguyur, maka tidak terjadi kemacetan di semua daerah.\r\n', 'Jika hujan deras mengguyur, maka tidak terjadi kemacetan di semua daerah.', 'Jika hujan deras mengguyur, maka terjadi kemacetan di sebagian daerah.', 'Jika hujan deras mengguyur, maka terjadi kemacetan di semua daerah.', '', 'E', 35),
(14, ' Perhatikan beberapa pernyataan berikut!<br>\r\n- Semua gunung memiliki hutan.<br>\r\n- Sebagian hutan yang ada di gunung adalah hutan lindung.<br>\r\nSimpulan yang tepat adalah …', 'Sebagian gunung memiliki hutan lindung dan sebagian lagi tidak memiliki hutan.', 'Sebagian hutan lindung berada di luar daerah gunung.', 'Sebagian gunung tidak memiliki hutan lindung. ', 'Semua gunung tidak memiliki hutan lindung.', 'Semua gunung memiliki hutan lindung.', '', 'C', 35),
(15, 'Kalimat yang ekuivalen dengan “Jika Risman pergi melaut siang ini, maka cuaca sedang tidak terik” \r\nadalah…', ' Risman tidak pergi melaut siang ini dan cuaca sedang tidak terik.', ' Cuaca sedang tidak terik atau Risman tidak pergi melaut siang ini.', ' Risman tidak pergi melaut siang ini atau cuaca sedang tidak terik.', ' Risman pergi melaut siang ini atau cuaca sedang terik.', ' Cuaca sedang terik dan Risman pergi pergi hari ini.', '', 'B', 35),
(16, 'Perhatikan ilustrasi berikut!<br>\r\nSetiap hari Minggu pagi, Taman SukaSuka mengadakan senam bersama dan sarapan gratis untuk\r\numum. Semua orang yang telah mengikuti senam dapat mengambil sarapan gratis dan harus \r\ntertib antrian. Berikut ini informasi terkait beberapa peserta senam.<br>\r\n- Sarah berada pada urutan kedua di depan Uzu, sedangkan Uzu berbaris di belakang Dinda.<br>\r\n- Mei berada pada baris kelima di depan Uzu atau tepatnya baris kedua di belakang Herdi.<br>\r\n- Tiap orang membutuhkan waktu tepat 2 menit untuk memilih makanan.<br>\r\nBerdasarkan ilustrasi tersebut, Sarah mendapat giliran memilih makanan … menit setelah Herdi.', ' 4\r\n', ' 6', '8', '10', '12', '', 'D', 35),
(17, 'Perhatikan ilustrasi berikut!<br>\r\nSuatu konferensi dihadiri 8 orang perwakilan negara, di antaranya 2 orang dari Indonesia, 1 orang \r\ndari Singapura, 2 orang dari Malaysia, 1 orang dari Brunei Darussalam, 1 orang dari Thailand, dan \r\n1 orang dari Filipina. Tiap perwakilan dari negara yang sama duduk bersebelahan.<br>\r\nJika perwakilan dari Singapura duduk berhadapan dengan perwakilan dari Filipina, pernyataan \r\nberikut yang <b>tidak</b> mungkin terjadi adalah…', 'Perwakilan dari Filipina duduk di antara perwakilan dari Indonesia dan Brunei Darussalam.', 'Perwakilan dari Singapura duduk di antara perwakilan dari Thailand dan Malaysia.', 'Kedua perwakilan dari Malaysia duduk berhadapan dengan perwakilan-perwakilan dari Indonesia.', 'Perwakilan dari Indonesia duduk bersebelahan dengan perwakilan dari Malaysia.', 'Perwakilan dari Thailand duduk bersebalahan dengan perwakilan dari Filipina.', '', 'D', 35),
(18, 'Perhatikan ilustrasi berikut!<br>\r\nKevin, Lulu, Made, Nia, dan Oxy telah mengikuti sebuah tes uji coba Ujian Nasional. Nilai Madel \r\nlebih tinggi daripada Oxy dan Kevin. Nilai Lulu lebih rendah daripada Nia dan Oxy. Nilai Nia lebih \r\ntinggi daripada Kevin, tetapi lebih rendah daripada Oxy.<br>\r\nJika tidak ada dua orang yang mendapatkan nilai yang sama, pernyataan berikut ini yang tidak \r\nmungkin terjadi adalah…', 'Lulu mendapatkan nilai terendah', 'Nilai Kevin lebih tinggi daripada Lulu\r\n', 'Nilai Oxy berada di antara Made dan Nia', 'Nilai Nila berada di urutan tengah-tengah', 'Nilai Kevin berada di urutan tertinggi kedua', '', 'E', 35),
(19, 'Perhatikan ilustrasi berikut!<br>\r\nRiana biasa melakukan beberapa kegiatan sebelum tidur, yaitu makan malam, minum susu, \r\nmenggosok gigi, mandi, atau membaca buku. Ia selalu makan malam, tetapi tidak selalu minum \r\nsusu. Apabila ia minum susu, ia melakukannya setelah makan makam. Menggosok gigi dilakukan \r\nsetelah Riana selesai makan malam atau minum susu. Setelah selesai menggosok gigi, ia mandi \r\natau membaca buku, tapi tidak keduanya. Riana baru bisa tidur setelah selesai mandi atau \r\nmembaca buku.<br>\r\nUrutan kegiatan berikut yang <b>tidak</b> mungkin Riana lakukan agar ia bisa tidur adalah…', 'Makan malam – menggosok gigi – mandi – tidur ', 'Makan malam – menggosok gigi – mambaca buku – tidur \r\n', ' Makan malam – minum susu – menggosok gigi – mandi – tidur ', 'Makan malam – minum susu – menggosok gigi – membaca buku – tidur ', 'Makan malam – menggosok gigi – minum susu – membaca buku – tidur ', '', 'E', 35),
(20, 'Perhatikan ilustrasi berikut!<br>\r\nSepty, Rian, dan Alya masing-masing membeli pupen di toko alat tulis A, B, dan C. pulpen yang \r\ndibeli Septy lebih banyak dari pulpen yang dibeli Alya, tetapi tidak melebihi banyak pulpen yang \r\ndibeli Rian. Saat membayar pulpen yang mereka beli, uang yang dibayarkan Alya lebih banyak dari \r\nSepty, tetapi tidak lebih banyak dari Rian.<br>\r\nPernyataan berikut yang PASTI BENAR adalah…', 'Harga pulpen di toko A lebih muran dari harga pulpen di toko C', 'Harga pulpen di toko B lebih mahal dari harga pulpen di toko C', 'Toko C menjual pulpen dengan harga yang paling mahal', 'Toko A menjual pulpun dengan harga yang paling murah', 'Toko A dan B menjual pulpen dengan harga yang sama', '', 'A', 35),
(21, 'Perhatikan ilustrasi berikut!<br>\r\nAmin, Bety, Cokro, Dian, dan Eko mencalonkan diri sebagai ketua OSIS. Bety, Cokro, dan Dian \r\nmengusung program Keterampilan Siswa. Amin, Cokro, dan Eko mengusung program Olahraga \r\nbagi Siswa. Amin dan Bety mengusung program Sukses Akademik. Dian dan Eko mengusung \r\nprogram Kebersamaan Guru dan Siswa. Setiap siswa hanya memilih satu nama sebagai ketua dan \r\ndidapat hasil pemungutan suara sebagai berikut. <br>\r\n1. Program Sukses Akademik dipilih oleh 50 siswa. <br>\r\n2. Program Keterampilan Siswa dipilih oleh 45 siswa. <br>\r\n3. Program Kebersamaan Guru dan Siswa dipilih 30 siswa. <br>\r\n4. Program Olahraga begi Siswa dipilih oleh 60 siswa. <br>\r\nJika jumlah suara dibagi sama banyak kepada setiap calon yang mengusung program yang sama, \r\ncalon yang paling banyak dipilih oleh siswa adalah…', 'Amin', 'Bety', 'Cokro', 'Dian', 'Eko', '', 'A', 50),
(22, 'Hasil dari 3 (<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{10}\" border=\"0\"/> + <img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{40}\" border=\"0\"/> + <img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{88}\" border=\"0\"/> + <img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{54}\" border=\"0\"/> + .. + <img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{40.600}\" border=\"0\"/>) adalah…', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{101}{200}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{99}{200}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{404}{406}\" border=\"0\"/>\r\n', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{205}{406}\" border=\"0\"/>', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{201}{406}\" border=\"0\"/>', '', 'E', 50),
(23, 'Perhatikan ilustrasi berikut!<br>\r\nPak Toni merupakan seorang karyawan swasta dengan gaji Rp4.500.00,00 per bulan. Setiap bulan, \r\ndia selalu mendapat bonus penjualan sebesar 10% dari gajinya. <br>\r\nPak Tono merupakan seorang pedagang buah. Dia membeli mangga dan jeruk berturut-turut \r\nseharga Rp28.000,00 per kg dan Rp34.500,00 per kg serta menjualnya dengan harga Rp33.000,00 \r\nper kg untuk mangga dan Rp42.000,00 per kg untuk jeruk.<br>\r\nJika setiap mangga dan jeruk yang dibeli Pak Tono laku terjual, kondisi berikut yang menyebabkan \r\nkeuntungan Pak Tono lebih besar daripada penghasilan bulanan Pak Toni adalah…', 'Terjual 320 kg mangga dan 420 kg jeruk.', 'Terjual 390 kg mangga dan 400 kg jeruk.', 'Terjual 400 kg mangga dan 390 kg jeruk.', 'Terjual 500 kg mangga dan 330 kg jeruk.', 'Terjual 550 kg mangga dan 290 kg jeruk.', '', 'D', 50),
(24, 'Urutan bilangan berikut dari yang terkecil hingga terbesar yang benar adalah…', '<img src=\"https://latex.codecogs.com/svg.image?\\frac{4}{5}\" border=\"0\"/>; 0,333 ; 10<math>\r\n	<sup>-1</sup>\r\n</math>; 60% ; 1', '0,333 ; 1<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{4}\" border=\"0\"/>; 10<math>\r\n	<sup>-1</sup>\r\n</math>; 60%', '10<math>\r\n	<sup>-1</sup>\r\n</math>; 0,333 ; 60% ; <img src=\"https://latex.codecogs.com/svg.image?\\frac{4}{5}\" border=\"0\"/>; 1<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{4}\" border=\"0\"/>', '1<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{4}\" border=\"0\"/>; 60% ; 10<math>\r\n	<sup>-1</sup>\r\n</math>; 0,333 ; <img src=\"https://latex.codecogs.com/svg.image?\\frac{4}{5}\" border=\"0\"/>', '60% ; 10<math>\r\n	<sup>-1</sup>\r\n</math>; <img src=\"https://latex.codecogs.com/svg.image?\\frac{4}{5}\" border=\"0\"/>; 0,333 ; 1<img src=\"https://latex.codecogs.com/svg.image?\\frac{1}{4}\" border=\"0\"/>', '', 'C', 50),
(25, 'Diberikan grafik rata-rata harga beras di penggilingan dua tahun terakhir seperti berikut.<br>\r\nberdasarkan grafik tersebut, pernyataan berikut yang BENAR \r\nadalah…', 'Persentase peningkatan rata-rata harga beras kualitas \r\npremium lebih rendah daripada persentase peningkatan ratarata harga beras kualitas medium pada dua tahun terakhir.', 'Rata harga beras per kg untuk kualitas luar di penggilingan \r\npada tahun 2021 meningkat 3% dari tahun sebelumnya.', 'Persentase kenaikan terendah dari peningkatan rata-rata harga beras per kg di penggilingan pada tahun 2021 terjadi \r\npada beras kualitas premium.', 'Rata-rata harga beras per kg untuk kualitas luar di penggilingan \r\npada tahun 2021 lebih rendah daripada rata-rata harga beras per kg untuk kualitas medium pada tahun 2020.', 'Selisih dari rata-rata harga beras per kg untuk kualitas medium dan premium pada tahun 2021 \r\nadalah Rp345,00.', 'n25.jpg', 'A', 50),
(26, 'Berdasarkan informasi di bawah, urutan rumah dengan besar kenaikan harga dari tahun 2012 sampai tahun 2022 dari yang paling besar adalah…', 'A, C, D, B ', 'C, D, B, A', 'A, C, B, D', ' C, D, A, B', 'A, B, D, C', 'n26.jpg', 'B', 50),
(27, ' Perhatikan barisan bilangan berikut!<br>\r\n1, 1, 5, 6, 4, 10, 11, 9, 15, 16, p, q, …<br>\r\nPernyataan berikut yang benar mengenai hubungan p dan q adalah …', '3p – 2q < 0', '2p + q < 50', '3p < 2q +5', 'q + 10 < p', 'p < 2q – 5', '', 'E', 20),
(28, ' Perhatikan barisan bilangan berikut!<br>\r\n4, 6, 10, 14, 22, 26, 34, a, b, …<br>\r\nNilai yang tepat untuk a dan b adalah…', '36 dan 40', '36 dan 42', '38 dan 42', '38 dan 46', '40 dan 46', '', 'D', 20),
(29, 'Berdasarkan pola gambar di bawah, nilai x yang mungkin adalah…', '11', '18', '23', '25', '53', 'n29.jpg', 'B', 20),
(30, 'Berdasarkan pola gambar di bawah, angka yang tepat untuk mengisi tanda tanya (?) adalah…', '16', '80', '100', '128', '25', 'n30.jpg', 'E', 20);

